package classWork5;

import java.util.Scanner;

public class replacingStrings {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		Scanner keyInput = new Scanner(System.in);
		for (i = keyInput.nextInt(); i >= 0; i--) {
			for (int x = i; x > 0; x--) {
				System.out.print(i);
			}
			System.out.println();

		}
	}

}
